import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

// Fix for default markers
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

// Custom drone icon
const createDroneIcon = (heading) => {
  return L.divIcon({
    html: `
      <div style="transform: rotate(${heading}deg); transition: transform 0.5s; width: 30px; height: 30px;">
        <div style="
          width: 100%; 
          height: 100%; 
          background: #3B82F6; 
          clip-path: polygon(50% 0%, 100% 38%, 82% 100%, 18% 100%, 0% 38%);
          display: flex;
          align-items: center;
          justify-content: center;
          color: white;
          font-size: 12px;
        ">
          üöÅ
        </div>
      </div>
    `,
    className: 'drone-icon',
    iconSize: [30, 30],
    iconAnchor: [15, 15],
  });
};

const LiveMap = ({ telemetry }) => {
  const position = telemetry.lat && telemetry.lon 
    ? [telemetry.lat, telemetry.lon] 
    : [51.5074, -0.1278];

  return (
    <div className="bg-gray-800 rounded-xl border border-gray-700 overflow-hidden">
      {/* Map Header */}
      <div className="flex items-center justify-between p-4 border-b border-gray-700">
        <div className="flex items-center space-x-3">
          <span className="text-lg">üó∫Ô∏è</span>
          <div>
            <h3 className="text-white font-semibold">Live Drone Position</h3>
            <p className="text-gray-400 text-sm">Real-time tracking</p>
          </div>
        </div>
        <div className="flex items-center space-x-4 text-sm">
          <div className="flex items-center space-x-2">
            <span>üß≠</span>
            <span className="text-gray-300">HDG: {telemetry.heading ? Math.round(telemetry.heading) : 'N/A'}¬∞</span>
          </div>
          <div className="flex items-center space-x-2">
            <span>‚ö°</span>
            <span className="text-gray-300">{telemetry.groundspeed ? telemetry.groundspeed.toFixed(1) : '0.0'} m/s</span>
          </div>
        </div>
      </div>

      {/* Map Container */}
      <div className="h-96 relative">
        <MapContainer
          center={position}
          zoom={17}
          style={{ height: '100%', width: '100%' }}
          key={JSON.stringify(position)} // Force re-render on position change
        >
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a>'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          
          {/* Drone marker */}
          <Marker 
            position={position}
            icon={createDroneIcon(telemetry.heading || 0)}
          >
            <Popup>
              <div className="text-sm">
                <div className="font-semibold text-gray-900 mb-2">üöÅ Drone Position</div>
                <div className="space-y-1">
                  <div><strong>Lat:</strong> {telemetry.lat?.toFixed(6) || 'N/A'}</div>
                  <div><strong>Lon:</strong> {telemetry.lon?.toFixed(6) || 'N/A'}</div>
                  <div><strong>Alt:</strong> {telemetry.alt ? `${telemetry.alt.toFixed(1)}m` : 'N/A'}</div>
                  <div><strong>Speed:</strong> {telemetry.groundspeed ? `${telemetry.groundspeed.toFixed(1)} m/s` : 'N/A'}</div>
                  <div><strong>Heading:</strong> {telemetry.heading ? `${Math.round(telemetry.heading)}¬∞` : 'N/A'}</div>
                </div>
              </div>
            </Popup>
          </Marker>
        </MapContainer>

        {/* Coordinates Display */}
        <div className="absolute bottom-4 left-4 bg-black/80 text-white px-3 py-2 rounded-lg text-sm">
          <div>Lat: {telemetry.lat?.toFixed(6) || '51.5074'}</div>
          <div>Lon: {telemetry.lon?.toFixed(6) || '-0.1278'}</div>
        </div>
      </div>

      {/* Map Footer */}
      <div className="p-4 bg-gray-700/50 border-t border-gray-600">
        <div className="grid grid-cols-4 gap-4 text-sm">
          <div>
            <div className="text-gray-400">Mode</div>
            <div className="text-white font-medium">{telemetry.mode || 'GUIDED'}</div>
          </div>
          <div>
            <div className="text-gray-400">Altitude</div>
            <div className="text-white font-medium">{telemetry.alt ? `${telemetry.alt.toFixed(1)}m` : '100.0m'}</div>
          </div>
          <div>
            <div className="text-gray-400">Satellites</div>
            <div className="text-white font-medium">{telemetry.satellites || '12'}</div>
          </div>
          <div>
            <div className="text-gray-400">Battery</div>
            <div className="text-white font-medium">
              {telemetry.battery_remaining ? `${telemetry.battery_remaining}%` : '80%'}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LiveMap;
