import React from 'react';
import { useTelemetry } from '../../context/TelemetryContext';
import { useAuth } from '../../context/AuthContext';
import Sidebar from '../Layout/Sidebar';
import Header from '../Layout/Header';
import StatsGrid from '../Dashboard/StatsGrid';
import LiveMap from '../Map/LiveMap';
import TelemetryCharts from '../Charts/TelemetryCharts';
import StatusPanel from '../Dashboard/StatusPanel';
import VideoStream from '../Video/VideoStream';
import NetworkStatus from '../Dashboard/NetworkStatus';

const Dashboard = () => {
  const { telemetry, connectionStatus } = useTelemetry();
  const { networkStatus } = useAuth();

  return (
    <div className="flex h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900 text-white overflow-hidden">
      {/* Sidebar */}
      <Sidebar />
      
      {/* Main Content */}
      <div className="flex-1 flex flex-col overflow-hidden">
        <Header />
        
        {/* Network Status Bar */}
        <NetworkStatus />
        
        <div className="flex-1 overflow-y-auto p-6 space-y-6 animate-fade-in">
          {/* Stats Grid */}
          <StatsGrid telemetry={telemetry} />

          {/* Main Dashboard Grid */}
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
            {/* Left Column - Map */}
            <div className="lg:col-span-2 space-y-6">
              <LiveMap telemetry={telemetry} />
              <TelemetryCharts telemetry={telemetry} />
            </div>

            {/* Right Column - Status and Video */}
            <div className="space-y-6">
              <StatusPanel telemetry={telemetry} connectionStatus={connectionStatus} />
              <VideoStream />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
