import React, { useRef, useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import { Video, VideoOff, Camera, Zap } from 'lucide-react';

const VideoStream = () => {
  const videoRef = useRef(null);
  const [stream, setStream] = useState(null);
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [streamQuality, setStreamQuality] = useState('hd');

  const startVideo = async () => {
    setIsLoading(true);
    setError('');
    
    try {
      const constraints = {
        video: {
          width: { ideal: streamQuality === 'hd' ? 1280 : 640 },
          height: { ideal: streamQuality === 'hd' ? 720 : 480 },
          frameRate: { ideal: 30 }
        }
      };

      const mediaStream = await navigator.mediaDevices.getUserMedia(constraints);
      
      if (videoRef.current) {
        videoRef.current.srcObject = mediaStream;
        setStream(mediaStream);
      }
    } catch (err) {
      setError(`Cannot access camera: ${err.message}`);
      console.error('Camera error:', err);
    } finally {
      setIsLoading(false);
    }
  };

  const stopVideo = () => {
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
      setStream(null);
    }
  };

  const toggleQuality = () => {
    setStreamQuality(prev => prev === 'hd' ? 'sd' : 'hd');
    if (stream) {
      stopVideo();
      setTimeout(startVideo, 100);
    }
  };

  useEffect(() => {
    return () => {
      stopVideo();
    };
  }, []);

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className="bg-gray-800 rounded-xl border border-gray-700"
    >
      {/* Video Header */}
      <div className="flex items-center justify-between p-4 border-b border-gray-700">
        <div className="flex items-center space-x-3">
          <div className="p-2 bg-primary-500/20 rounded-lg">
            <Video className="h-5 w-5 text-primary-400" />
          </div>
          <div>
            <h3 className="text-white font-semibold">Live Video Stream</h3>
            <p className="text-gray-400 text-sm">Real-time drone camera feed</p>
          </div>
        </div>
        
        {/* Quality Selector */}
        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          onClick={toggleQuality}
          className={`flex items-center space-x-2 px-3 py-1.5 rounded-lg text-sm font-medium transition-all ${
            streamQuality === 'hd' 
              ? 'bg-blue-500/20 text-blue-400 border border-blue-500/30' 
              : 'bg-gray-600 text-gray-400 border border-gray-500'
          }`}
        >
          <Zap className="h-4 w-4" />
          <span>{streamQuality === 'hd' ? 'HD' : 'SD'}</span>
        </motion.button>
      </div>

      {/* Video Container */}
      <div className="p-4">
        <div className="relative bg-black rounded-lg overflow-hidden aspect-video">
          {stream ? (
            <motion.video
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              ref={videoRef}
              autoPlay
              playsInline
              muted
              className="w-full h-full object-cover"
            />
          ) : (
            <div className="absolute inset-0 flex items-center justify-center bg-gray-900">
              <div className="text-center">
                <Camera className="h-12 w-12 text-gray-600 mx-auto mb-3" />
                <p className="text-gray-400 text-sm">Video stream not active</p>
                <p className="text-gray-500 text-xs mt-1">Click start to begin streaming</p>
              </div>
            </div>
          )}

          {/* Video Overlay Info */}
          {stream && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              className="absolute top-3 left-3 bg-black/70 text-white px-2 py-1 rounded text-xs"
            >
              LIVE
            </motion.div>
          )}

          {/* Loading Overlay */}
          {isLoading && (
            <div className="absolute inset-0 bg-black/50 flex items-center justify-center">
              <div className="text-center">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-white mx-auto mb-2"></div>
                <p className="text-white text-sm">Starting stream...</p>
              </div>
            </div>
          )}
        </div>

        {/* Error Message */}
        {error && (
          <motion.div
            initial={{ opacity: 0, scale: 0.95 }}
            animate={{ opacity: 1, scale: 1 }}
            className="mt-3 bg-red-500/20 border border-red-500/30 rounded-lg p-3"
          >
            <div className="flex items-center space-x-2">
              <VideoOff className="h-4 w-4 text-red-400" />
              <span className="text-red-200 text-sm">{error}</span>
            </div>
          </motion.div>
        )}

        {/* Video Controls */}
        <div className="flex space-x-3 mt-4">
          {!stream ? (
            <motion.button
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
              onClick={startVideo}
              disabled={isLoading}
              className="flex-1 bg-gradient-to-r from-green-500 to-emerald-600 text-white py-2.5 px-4 rounded-lg font-semibold flex items-center justify-center space-x-2 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              <Video className="h-4 w-4" />
              <span>Start Video Stream</span>
            </motion.button>
          ) : (
            <motion.button
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
              onClick={stopVideo}
              className="flex-1 bg-gradient-to-r from-red-500 to-pink-600 text-white py-2.5 px-4 rounded-lg font-semibold flex items-center justify-center space-x-2"
            >
              <VideoOff className="h-4 w-4" />
              <span>Stop Video Stream</span>
            </motion.button>
          )}
        </div>

        {/* Stream Info */}
        <div className="mt-3 grid grid-cols-3 gap-2 text-xs text-gray-400">
          <div className="text-center p-2 bg-gray-700/50 rounded">
            <div>Quality</div>
            <div className="text-white font-medium">{streamQuality.toUpperCase()}</div>
          </div>
          <div className="text-center p-2 bg-gray-700/50 rounded">
            <div>Status</div>
            <div className="text-white font-medium">{stream ? 'Live' : 'Offline'}</div>
          </div>
          <div className="text-center p-2 bg-gray-700/50 rounded">
            <div>FPS</div>
            <div className="text-white font-medium">{stream ? '30' : '0'}</div>
          </div>
        </div>
      </div>

      {/* Video Footer Info */}
      <div className="p-4 bg-gray-700/50 border-t border-gray-600">
        <p className="text-gray-400 text-sm text-center">
          This simulates the drone camera feed using your webcam. 
          In a real setup, this would be a WebRTC stream from the drone.
        </p>
      </div>
    </motion.div>
  );
};

export default VideoStream;
