import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Fix for default markers
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

const LiveMap = ({ telemetry }) => {
  const position = telemetry.lat && telemetry.lon 
    ? [telemetry.lat, telemetry.lon] 
    : [51.5074, -0.1278]; // Default to London

  return (
    <div className="map-container">
      <h3>Live Drone Position</h3>
      <MapContainer
        center={position}
        zoom={17}
        style={{ height: '400px', width: '100%' }}
      >
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a>'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        <Marker position={position}>
          <Popup>
            üöÅ Drone Position<br />
            Lat: {telemetry.lat?.toFixed(6) || 'N/A'}<br />
            Lon: {telemetry.lon?.toFixed(6) || 'N/A'}<br />
            Alt: {telemetry.alt ? `${telemetry.alt.toFixed(1)}m` : 'N/A'}<br />
            Speed: {telemetry.groundspeed ? `${telemetry.groundspeed.toFixed(1)} m/s` : 'N/A'}
          </Popup>
        </Marker>
      </MapContainer>
    </div>
  );
};

export default LiveMap;
