import React, { useRef, useEffect, useState } from 'react';

const VideoStream = () => {
  const videoRef = useRef(null);
  const [stream, setStream] = useState(null);
  const [error, setError] = useState('');

  const startVideo = async () => {
    try {
      const mediaStream = await navigator.mediaDevices.getUserMedia({ 
        video: { width: 640, height: 480 } 
      });
      
      if (videoRef.current) {
        videoRef.current.srcObject = mediaStream;
        setStream(mediaStream);
      }
    } catch (err) {
      setError(`Cannot access camera: ${err.message}`);
    }
  };

  const stopVideo = () => {
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
      setStream(null);
    }
  };

  useEffect(() => {
    return () => {
      stopVideo();
    };
  }, []);

  return (
    <div className="video-stream">
      <h3>Live Video Stream</h3>
      
      <div className="video-container">
        {stream ? (
          <video
            ref={videoRef}
            autoPlay
            playsInline
            muted
            className="video-element"
          />
        ) : (
          <div className="video-placeholder">
            <p>Click Start to begin streaming</p>
          </div>
        )}
      </div>

      {error && (
        <div className="video-error">
          {error}
        </div>
      )}

      <div className="video-controls">
        {!stream ? (
          <button onClick={startVideo} className="btn btn-start">
            Start Video
          </button>
        ) : (
          <button onClick={stopVideo} className="btn btn-stop">
            Stop Video
          </button>
        )}
      </div>

      <div className="video-info">
        <p>This simulates drone camera using your webcam</p>
        <p>Real implementation would use WebRTC with drone</p>
      </div>
    </div>
  );
};

export default VideoStream;
