import React, { createContext, useState, useContext, useEffect } from 'react';

const TelemetryContext = createContext();

export const useTelemetry = () => {
  const context = useContext(TelemetryContext);
  if (!context) {
    throw new Error('useTelemetry must be used within a TelemetryProvider');
  }
  return context;
};

export const TelemetryProvider = ({ children }) => {
  const [telemetry, setTelemetry] = useState({});
  const [connectionStatus, setConnectionStatus] = useState('disconnected');
  const [websocket, setWebsocket] = useState(null);
  const [messageHistory, setMessageHistory] = useState([]);
  const [connectionStats, setConnectionStats] = useState({
    messagesReceived: 0,
    connectedSince: null,
    lastMessage: null
  });

  useEffect(() => {
    connectWebSocket();
    
    return () => {
      if (websocket) {
        websocket.close();
      }
    };
  }, []);

  const connectWebSocket = () => {
    const ws = new WebSocket('ws://localhost:8765');
    
    ws.onopen = () => {
      setConnectionStatus('connected');
      setConnectionStats(prev => ({
        ...prev,
        connectedSince: new Date().toISOString()
      }));
      console.log('âœ… WebSocket connected to Urban Mirtalx GCS');
    };

    ws.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);
        
        if (data.type === 'telemetry') {
          setTelemetry(prev => ({ ...prev, ...data.data }));
        }
        
        // Update message history
        setMessageHistory(prev => [...prev.slice(-99), {
          type: data.type,
          timestamp: new Date().toLocaleTimeString(),
          data: data.type === 'telemetry' ? 'Telemetry update' : data
        }]);
        
        // Update connection stats
        setConnectionStats(prev => ({
          ...prev,
          messagesReceived: prev.messagesReceived + 1,
          lastMessage: new Date().toISOString()
        }));
        
      } catch (error) {
        console.error('âŒ Error parsing telemetry:', error);
      }
    };

    ws.onclose = () => {
      setConnectionStatus('disconnected');
      console.log('ðŸ”Œ WebSocket disconnected');
      setTimeout(connectWebSocket, 3000);
    };

    ws.onerror = (error) => {
      console.error('âŒ WebSocket error:', error);
      setConnectionStatus('error');
    };

    setWebsocket(ws);
  };

  const sendCommand = (command, data = {}) => {
    if (websocket && connectionStatus === 'connected') {
      const message = {
        type: 'command',
        command,
        ...data,
        timestamp: Date.now()
      };
      websocket.send(JSON.stringify(message));
      
      // Add to message history
      setMessageHistory(prev => [...prev.slice(-99), {
        type: 'command',
        timestamp: new Date().toLocaleTimeString(),
        data: `Sent command: ${command}`
      }]);
    }
  };

  const value = {
    telemetry,
    connectionStatus,
    messageHistory,
    connectionStats,
    sendCommand
  };

  return (
    <TelemetryContext.Provider value={value}>
      {children}
    </TelemetryContext.Provider>
  );
};
