import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import { useTelemetry } from '../../context/TelemetryContext';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Fix for default markers
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

// Custom drone icon with animations
const createDroneIcon = (heading, armed, isConnected, battery) => {
  const batteryColor = battery > 50 ? '#10b981' : battery > 20 ? '#f59e0b' : '#ef4444';
  const pulseAnimation = armed ? 'pulse 1s infinite' : 'none';
  const connectionGlow = isConnected ? 'glow 2s infinite' : 'none';
  
  return L.divIcon({
    html: `
      <div class="drone-container" style="transform: rotate(${heading}deg);">
        <div class="drone-icon ${armed ? 'armed' : ''} ${isConnected ? 'connected' : ''}" 
             style="animation: ${pulseAnimation}, ${connectionGlow}; border-color: ${batteryColor};">
          üöÅ
        </div>
        ${armed ? '<div class="propeller left-propeller"></div><div class="propeller right-propeller"></div>' : ''}
        <div class="status-indicator ${armed ? 'armed' : 'disarmed'}"></div>
      </div>
      <style>
        .drone-container {
          position: relative;
          width: 48px;
          height: 48px;
          transition: transform 0.3s ease;
        }
        
        .drone-icon {
          width: 40px;
          height: 40px;
          background: var(--drone-bg, #3B82F6);
          border-radius: 50%;
          border: 3px solid;
          display: flex;
          align-items: center;
          justify-content: center;
          font-size: 18px;
          box-shadow: 0 4px 15px rgba(0,0,0,0.3);
          transition: all 0.3s ease;
          position: relative;
          z-index: 2;
        }
        
        .drone-icon.armed {
          --drone-bg: #ef4444;
          box-shadow: 0 4px 20px rgba(239, 68, 68, 0.4);
        }
        
        .drone-icon.connected {
          box-shadow: 0 4px 20px rgba(59, 130, 246, 0.6);
        }
        
        .propeller {
          position: absolute;
          width: 12px;
          height: 12px;
          background: #6b7280;
          border-radius: 50%;
          animation: spin 0.8s linear infinite;
          z-index: 1;
        }
        
        .left-propeller {
          top: 5px;
          left: -8px;
        }
        
        .right-propeller {
          top: 5px;
          right: -8px;
        }
        
        .status-indicator {
          position: absolute;
          bottom: -2px;
          left: 50%;
          transform: translateX(-50%);
          width: 8px;
          height: 8px;
          border-radius: 50%;
          border: 2px solid white;
        }
        
        .status-indicator.armed {
          background: #ef4444;
          animation: blink 1s infinite;
        }
        
        .status-indicator.disarmed {
          background: #10b981;
        }
        
        @keyframes pulse {
          0%, 100% { transform: scale(1); }
          50% { transform: scale(1.1); }
        }
        
        @keyframes glow {
          0%, 100% { box-shadow: 0 4px 20px rgba(59, 130, 246, 0.6); }
          50% { box-shadow: 0 4px 30px rgba(59, 130, 246, 0.8); }
        }
        
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }
        
        @keyframes blink {
          0%, 100% { opacity: 1; }
          50% { opacity: 0.3; }
        }
      </style>
    `,
    className: 'custom-drone-marker',
    iconSize: [48, 48],
    iconAnchor: [24, 24],
  });
};

const LiveMap = () => {
  const { telemetry, connectionStatus } = useTelemetry();
  const [popupData, setPopupData] = useState(null);
  const [mapCenter, setMapCenter] = useState([12.9716, 77.5946]);

  // Default to Bangalore coordinates
  const defaultPosition = [12.9716, 77.5946];
  const position = telemetry.lat && telemetry.lon 
    ? [telemetry.lat, telemetry.lon] 
    : defaultPosition;

  // Update map center when telemetry changes
  useEffect(() => {
    if (telemetry.lat && telemetry.lon) {
      setMapCenter([telemetry.lat, telemetry.lon]);
    }
  }, [telemetry.lat, telemetry.lon]);

  // Format telemetry data for display
  const formatTelemetryData = () => {
    const data = {
      position: {
        lat: position[0].toFixed(6),
        lon: position[1].toFixed(6),
        alt: telemetry.alt ? `${telemetry.alt.toFixed(1)}m` : '100.0m'
      },
      movement: {
        speed: telemetry.groundspeed ? `${telemetry.groundspeed.toFixed(1)} m/s` : '0.0 m/s',
        heading: telemetry.heading ? `${Math.round(telemetry.heading)}¬∞` : '0¬∞',
        airspeed: telemetry.airspeed ? `${telemetry.airspeed.toFixed(1)} m/s` : 'N/A'
      },
      status: {
        mode: telemetry.mode || 'GUIDED',
        armed: telemetry.armed ? 'ARMED üî¥' : 'DISARMED üü¢',
        system: telemetry.system_status || 'STANDBY'
      },
      battery: {
        level: telemetry.battery_remaining ? `${telemetry.battery_remaining.toFixed(1)}%` : '95.0%',
        voltage: telemetry.voltage_battery ? `${telemetry.voltage_battery.toFixed(2)}V` : '12.60V',
        current: telemetry.current_battery ? `${telemetry.current_battery.toFixed(1)}A` : '0.0A'
      },
      gps: {
        satellites: telemetry.satellites || '15',
        fix: '3D Fix',
        hdop: telemetry.eph ? telemetry.eph.toFixed(1) : '1.5',
        vdop: telemetry.epv ? telemetry.epv.toFixed(1) : '2.0'
      },
      communication: {
        rssi: telemetry.rssi ? `${telemetry.rssi.toFixed(0)} dBm` : '-55 dBm',
        noise: telemetry.noise ? `${telemetry.noise.toFixed(0)} dBm` : '-80 dBm',
        snr: telemetry.rssi && telemetry.noise ? `${(telemetry.rssi - telemetry.noise).toFixed(0)} dB` : '25 dB'
      },
      attitude: {
        roll: telemetry.roll ? `${telemetry.roll.toFixed(1)}¬∞` : '0.0¬∞',
        pitch: telemetry.pitch ? `${telemetry.pitch.toFixed(1)}¬∞` : '0.0¬∞',
        yaw: telemetry.yaw ? `${telemetry.yaw.toFixed(1)}¬∞` : '0.0¬∞'
      }
    };
    
    return data;
  };

  const telemetryData = formatTelemetryData();

  const mapStyles = {
    container: {
      background: 'rgba(30, 41, 59, 0.8)',
      borderRadius: '15px',
      padding: '20px',
      backdropFilter: 'blur(10px)',
      border: '1px solid rgba(255, 255, 255, 0.1)',
      marginBottom: '20px'
    },
    header: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      marginBottom: '15px',
      paddingBottom: '15px',
      borderBottom: '1px solid rgba(255, 255, 255, 0.1)'
    },
    coordinates: {
      display: 'flex',
      gap: '15px',
      fontSize: '12px',
      color: '#94a3b8',
      background: 'rgba(15, 23, 42, 0.6)',
      padding: '8px 12px',
      borderRadius: '6px',
      border: '1px solid rgba(255, 255, 255, 0.05)'
    },
    map: {
      height: '400px',
      width: '100%',
      borderRadius: '10px',
      overflow: 'hidden',
      border: '2px solid rgba(255, 255, 255, 0.1)'
    },
    footer: {
      marginTop: '15px',
      padding: '12px 15px',
      background: 'rgba(15, 23, 42, 0.6)',
      borderRadius: '8px',
      border: '1px solid rgba(255, 255, 255, 0.05)',
      fontSize: '12px',
      color: '#94a3b8',
      textAlign: 'center'
    },
    popup: {
      minWidth: '280px',
      background: 'rgba(15, 23, 42, 0.95)',
      borderRadius: '10px',
      border: '1px solid rgba(59, 130, 246, 0.3)',
      backdropFilter: 'blur(10px)'
    },
    popupHeader: {
      background: 'linear-gradient(90deg, #3b82f6, #8b5cf6)',
      color: 'white',
      padding: '12px 15px',
      margin: '-10px -10px 10px -10px',
      borderRadius: '8px 8px 0 0',
      fontSize: '14px',
      fontWeight: '600',
      display: 'flex',
      alignItems: 'center',
      gap: '8px'
    },
    section: {
      marginBottom: '12px',
      paddingBottom: '12px',
      borderBottom: '1px solid rgba(255, 255, 255, 0.1)'
    },
    sectionTitle: {
      fontSize: '11px',
      color: '#3b82f6',
      textTransform: 'uppercase',
      letterSpacing: '0.5px',
      marginBottom: '8px',
      fontWeight: '600',
      display: 'flex',
      alignItems: 'center',
      gap: '6px'
    },
    dataGrid: {
      display: 'grid',
      gridTemplateColumns: '1fr 1fr',
      gap: '8px',
      fontSize: '11px'
    },
    dataItem: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      padding: '4px 0'
    },
    dataLabel: {
      color: '#94a3b8',
      fontWeight: '500'
    },
    dataValue: {
      color: '#e2e8f0',
      fontWeight: '600',
      textAlign: 'right'
    },
    statusBadge: {
      padding: '2px 8px',
      borderRadius: '12px',
      fontSize: '10px',
      fontWeight: '600',
      textTransform: 'uppercase'
    },
    armedBadge: {
      background: 'rgba(239, 68, 68, 0.2)',
      color: '#ef4444',
      border: '1px solid rgba(239, 68, 68, 0.3)'
    },
    disarmedBadge: {
      background: 'rgba(16, 185, 129, 0.2)',
      color: '#10b981',
      border: '1px solid rgba(16, 185, 129, 0.3)'
    }
  };

  return (
    <div style={mapStyles.container}>
      <div style={mapStyles.header}>
        <h3 style={{ margin: 0, fontSize: '16px', fontWeight: '600', color: '#e2e8f0' }}>
          üó∫Ô∏è Live Drone Position
        </h3>
        <div style={mapStyles.coordinates}>
          <span>Lat: {position[0].toFixed(6)}</span>
          <span>Lon: {position[1].toFixed(6)}</span>
        </div>
      </div>
      
      <MapContainer
        center={mapCenter}
        zoom={15}
        style={mapStyles.map}
        className="live-map"
      >
        {/* Satellite View */}
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a>'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        
        {/* Drone Marker */}
        <Marker 
          position={position}
          icon={createDroneIcon(
            telemetry.heading || 0, 
            telemetry.armed, 
            connectionStatus === 'connected',
            telemetry.battery_remaining || 95
          )}
          eventHandlers={{
            click: () => {
              setPopupData(telemetryData);
            }
          }}
        >
          <Popup 
            closeButton={true}
            className="custom-popup"
            autoPan={true}
          >
            <div style={mapStyles.popup}>
              <div style={mapStyles.popupHeader}>
                üöÅ Drone Telemetry
              </div>
              
              {/* Position Section */}
              <div style={mapStyles.section}>
                <div style={mapStyles.sectionTitle}>üìç Position & Altitude</div>
                <div style={mapStyles.dataGrid}>
                  <div style={mapStyles.dataItem}>
                    <span style={mapStyles.dataLabel}>Latitude:</span>
                    <span style={mapStyles.dataValue}>{telemetryData.position.lat}</span>
                  </div>
                  <div style={mapStyles.dataItem}>
                    <span style={mapStyles.dataLabel}>Longitude:</span>
                    <span style={mapStyles.dataValue}>{telemetryData.position.lon}</span>
                  </div>
                  <div style={mapStyles.dataItem}>
                    <span style={mapStyles.dataLabel}>Altitude:</span>
                    <span style={mapStyles.dataValue}>{telemetryData.position.alt}</span>
                  </div>
                </div>
              </div>

              {/* Movement Section */}
              <div style={mapStyles.section}>
                <div style={mapStyles.sectionTitle}>‚ö° Movement</div>
                <div style={mapStyles.dataGrid}>
                  <div style={mapStyles.dataItem}>
                    <span style={mapStyles.dataLabel}>Ground Speed:</span>
                    <span style={mapStyles.dataValue}>{telemetryData.movement.speed}</span>
                  </div>
                  <div style={mapStyles.dataItem}>
                    <span style={mapStyles.dataLabel}>Air Speed:</span>
                    <span style={mapStyles.dataValue}>{telemetryData.movement.airspeed}</span>
                  </div>
                  <div style={mapStyles.dataItem}>
                    <span style={mapStyles.dataLabel}>Heading:</span>
                    <span style={mapStyles.dataValue}>{telemetryData.movement.heading}</span>
                  </div>
                </div>
              </div>

              {/* Status Section */}
              <div style={mapStyles.section}>
                <div style={mapStyles.sectionTitle}>üöÄ Status</div>
                <div style={mapStyles.dataGrid}>
                  <div style={mapStyles.dataItem}>
                    <span style={mapStyles.dataLabel}>Flight Mode:</span>
                    <span style={mapStyles.dataValue}>{telemetryData.status.mode}</span>
                  </div>
                  <div style={mapStyles.dataItem}>
                    <span style={mapStyles.dataLabel}>Armed:</span>
                    <span style={mapStyles.dataValue}>
                      <span style={{
                        ...mapStyles.statusBadge,
                        ...(telemetry.armed ? mapStyles.armedBadge : mapStyles.disarmedBadge)
                      }}>
                        {telemetryData.status.armed}
                      </span>
                    </span>
                  </div>
                  <div style={mapStyles.dataItem}>
                    <span style={mapStyles.dataLabel}>System:</span>
                    <span style={mapStyles.dataValue}>{telemetryData.status.system}</span>
                  </div>
                </div>
              </div>

              {/* Battery Section */}
              <div style={mapStyles.section}>
                <div style={mapStyles.sectionTitle}>üîã Battery</div>
                <div style={mapStyles.dataGrid}>
                  <div style={mapStyles.dataItem}>
                    <span style={mapStyles.dataLabel}>Level:</span>
                    <span style={{
                      ...mapStyles.dataValue,
                      color: (telemetry.battery_remaining || 95) > 50 ? '#10b981' : 
                             (telemetry.battery_remaining || 95) > 20 ? '#f59e0b' : '#ef4444'
                    }}>
                      {telemetryData.battery.level}
                    </span>
                  </div>
                  <div style={mapStyles.dataItem}>
                    <span style={mapStyles.dataLabel}>Voltage:</span>
                    <span style={mapStyles.dataValue}>{telemetryData.battery.voltage}</span>
                  </div>
                  <div style={mapStyles.dataItem}>
                    <span style={mapStyles.dataLabel}>Current:</span>
                    <span style={mapStyles.dataValue}>{telemetryData.battery.current}</span>
                  </div>
                </div>
              </div>

              {/* GPS Section */}
              <div style={mapStyles.section}>
                <div style={mapStyles.sectionTitle}>üõ∞Ô∏è GPS Info</div>
                <div style={mapStyles.dataGrid}>
                  <div style={mapStyles.dataItem}>
                    <span style={mapStyles.dataLabel}>Satellites:</span>
                    <span style={mapStyles.dataValue}>{telemetryData.gps.satellites}</span>
                  </div>
                  <div style={mapStyles.dataItem}>
                    <span style={mapStyles.dataLabel}>Fix Type:</span>
                    <span style={mapStyles.dataValue}>{telemetryData.gps.fix}</span>
                  </div>
                  <div style={mapStyles.dataItem}>
                    <span style={mapStyles.dataLabel}>HDOP/VDOP:</span>
                    <span style={mapStyles.dataValue}>{telemetryData.gps.hdop}/{telemetryData.gps.vdop}</span>
                  </div>
                </div>
              </div>

              {/* Communication Section */}
              <div style={mapStyles.section}>
                <div style={mapStyles.sectionTitle}>üì° Communication</div>
                <div style={mapStyles.dataGrid}>
                  <div style={mapStyles.dataItem}>
                    <span style={mapStyles.dataLabel}>RSSI:</span>
                    <span style={mapStyles.dataValue}>{telemetryData.communication.rssi}</span>
                  </div>
                  <div style={mapStyles.dataItem}>
                    <span style={mapStyles.dataLabel}>Noise:</span>
                    <span style={mapStyles.dataValue}>{telemetryData.communication.noise}</span>
                  </div>
                  <div style={mapStyles.dataItem}>
                    <span style={mapStyles.dataLabel}>SNR:</span>
                    <span style={mapStyles.dataValue}>{telemetryData.communication.snr}</span>
                  </div>
                </div>
              </div>

              {/* Attitude Section */}
              <div style={mapStyles.section}>
                <div style={mapStyles.sectionTitle}>üìä Attitude</div>
                <div style={mapStyles.dataGrid}>
                  <div style={mapStyles.dataItem}>
                    <span style={mapStyles.dataLabel}>Roll:</span>
                    <span style={mapStyles.dataValue}>{telemetryData.attitude.roll}</span>
                  </div>
                  <div style={mapStyles.dataItem}>
                    <span style={mapStyles.dataLabel}>Pitch:</span>
                    <span style={mapStyles.dataValue}>{telemetryData.attitude.pitch}</span>
                  </div>
                  <div style={mapStyles.dataItem}>
                    <span style={mapStyles.dataLabel}>Yaw:</span>
                    <span style={mapStyles.dataValue}>{telemetryData.attitude.yaw}</span>
                  </div>
                </div>
              </div>

              {/* Last Update */}
              <div style={{ fontSize: '10px', color: '#64748b', textAlign: 'center', marginTop: '10px' }}>
                Last Update: {telemetry.timestamp ? new Date(telemetry.timestamp * 1000).toLocaleTimeString() : new Date().toLocaleTimeString()}
              </div>
            </div>
          </Popup>
        </Marker>
      </MapContainer>
      
      <div style={mapStyles.footer}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', flexWrap: 'wrap', gap: '10px' }}>
          <span>üìç Bangalore, India</span>
          <span>üõ∞Ô∏è {telemetryData.gps.satellites} satellites</span>
          <span>üéØ {telemetryData.gps.fix}</span>
          <span>üåê MAVLink Protocol</span>
          <span>üì∂ Connection: {connectionStatus.toUpperCase()}</span>
        </div>
      </div>
    </div>
  );
};

export default LiveMap;
