import React from 'react';

const StatsGrid = ({ telemetry }) => {
  const stats = [
    {
      icon: 'üöÅ',
      label: 'Online Drones',
      value: '1/26',
      change: '+1',
      color: 'text-green-400',
      bg: 'from-green-500/20 to-green-500/20',
      border: 'border-green-500/30'
    },
    {
      icon: '‚è±Ô∏è',
      label: 'Flight Time',
      value: '682 hr',
      change: '+12h',
      color: 'text-blue-400',
      bg: 'from-blue-500/20 to-blue-500/20',
      border: 'border-blue-500/30'
    },
    {
      icon: 'üó∫Ô∏è',
      label: 'Flight Distance',
      value: '1.2k km',
      change: '+45km',
      color: 'text-purple-400',
      bg: 'from-purple-500/20 to-purple-500/20',
      border: 'border-purple-500/30'
    },
    {
      icon: 'üìä',
      label: 'Flights',
      value: '1,216',
      change: '+8',
      color: 'text-orange-400',
      bg: 'from-orange-500/20 to-orange-500/20',
      border: 'border-orange-500/30'
    },
    {
      icon: 'üîã',
      label: 'Battery',
      value: telemetry.battery_remaining ? `${telemetry.battery_remaining}%` : 'N/A',
      change: telemetry.battery_voltage ? `${telemetry.battery_voltage.toFixed(1)}V` : 'N/A',
      color: 'text-yellow-400',
      bg: 'from-yellow-500/20 to-yellow-500/20',
      border: 'border-yellow-500/30'
    },
    {
      icon: 'üõ∞Ô∏è',
      label: 'GPS Satellites',
      value: telemetry.satellites || '12',
      change: '3D Fix',
      color: 'text-cyan-400',
      bg: 'from-cyan-500/20 to-cyan-500/20',
      border: 'border-cyan-500/30'
    }
  ];

  return (
    <div className="grid grid-cols-2 lg:grid-cols-3 xl:grid-cols-6 gap-4">
      {stats.map((stat, index) => (
        <div
          key={stat.label}
          className={`bg-gradient-to-br ${stat.bg} border ${stat.border} rounded-xl p-4 backdrop-blur-sm hover:scale-105 transition-transform`}
        >
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-400 text-sm font-medium mb-1">{stat.label}</p>
              <p className="text-2xl font-bold text-white mb-1">{stat.value}</p>
              <p className={`text-xs font-medium ${stat.color}`}>{stat.change}</p>
            </div>
            <div className="p-2 rounded-lg bg-white/10">
              <span className="text-2xl">{stat.icon}</span>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default StatsGrid;
