import React, { createContext, useState, useContext, useEffect } from 'react';

const TelemetryContext = createContext();

export const useTelemetry = () => {
  const context = useContext(TelemetryContext);
  if (!context) {
    throw new Error('useTelemetry must be used within a TelemetryProvider');
  }
  return context;
};

export const TelemetryProvider = ({ children }) => {
  const [telemetry, setTelemetry] = useState({});
  const [connectionStatus, setConnectionStatus] = useState('disconnected');
  const [error, setError] = useState('');

  useEffect(() => {
    connectWebSocket();
    
    return () => {
      // Cleanup will happen automatically
    };
  }, []);

  const connectWebSocket = () => {
    try {
      const ws = new WebSocket('ws://localhost:8765');
      
      ws.onopen = () => {
        setConnectionStatus('connected');
        setError('');
        console.log('âœ… Connected to GCS WebSocket');
      };

      ws.onmessage = (event) => {
        try {
          const data = JSON.parse(event.data);
          
          if (data.type === 'telemetry') {
            setTelemetry(prev => ({ ...prev, ...data.data }));
          } else if (data.type === 'connection') {
            console.log('ðŸ”—', data.message);
          } else if (data.type === 'command_ack') {
            console.log('ðŸ“¡ Command result:', data);
          }
        } catch (error) {
          console.error('âŒ Error parsing message:', error);
        }
      };

      ws.onclose = () => {
        setConnectionStatus('disconnected');
        console.log('ðŸ”Œ WebSocket disconnected - attempting reconnect...');
        
        // Attempt reconnect after 2 seconds
        setTimeout(connectWebSocket, 2000);
      };

      ws.onerror = (error) => {
        console.error('âŒ WebSocket error:', error);
        setConnectionStatus('error');
        setError('WebSocket connection failed');
      };

    } catch (error) {
      console.error('âŒ WebSocket connection error:', error);
      setError('Failed to create WebSocket connection');
    }
  };

  const sendCommand = (command, params = {}) => {
    // For now, just log the command
    console.log('ðŸ“¡ Sending command:', command, params);
    
    // In a real implementation, this would send via WebSocket
    // But we'll simulate success for now
    return true;
  };

  const value = {
    telemetry,
    connectionStatus,
    error,
    sendCommand
  };

  return (
    <TelemetryContext.Provider value={value}>
      {children}
    </TelemetryContext.Provider>
  );
};
