import React from 'react';
import './StatusPanel.css';

const StatusPanel = ({ telemetry, connectionStatus }) => {
  const statusItems = [
    {
      label: 'Flight Mode',
      value: telemetry.mode || 'UNKNOWN',
      icon: 'üöÄ',
      color: '#3b82f6'
    },
    {
      label: 'Armed',
      value: telemetry.armed ? 'ARMED' : 'DISARMED',
      icon: telemetry.armed ? 'üî¥' : 'üü¢',
      color: telemetry.armed ? '#ef4444' : '#10b981'
    },
    {
      label: 'GPS Satellites',
      value: telemetry.satellites || '0',
      icon: 'üõ∞Ô∏è',
      color: '#8b5cf6'
    },
    {
      label: 'Battery',
      value: telemetry.battery_remaining ? `${telemetry.battery_remaining}%` : 'N/A',
      icon: 'üîã',
      color: telemetry.battery_remaining > 30 ? '#f59e0b' : '#ef4444'
    },
    {
      label: 'Altitude',
      value: telemetry.alt ? `${telemetry.alt.toFixed(1)}m` : 'N/A',
      icon: 'üìè',
      color: '#06b6d4'
    },
    {
      label: 'Speed',
      value: telemetry.groundspeed ? `${telemetry.groundspeed.toFixed(1)} m/s` : 'N/A',
      icon: '‚ö°',
      color: '#10b981'
    }
  ];

  return (
    <div className="status-panel">
      <h3>üöÄ Drone Status</h3>
      <div className="status-grid">
        {statusItems.map((item, index) => (
          <div key={index} className="status-item">
            <div className="status-icon" style={{ color: item.color }}>
              {item.icon}
            </div>
            <div className="status-info">
              <div className="status-label">{item.label}</div>
              <div className="status-value" style={{ color: item.color }}>
                {item.value}
              </div>
            </div>
          </div>
        ))}
      </div>
      
      {/* Connection Info */}
      <div className="connection-info">
        <div className="connection-item">
          <span>WebSocket:</span>
          <span className={`connection-status ${connectionStatus}`}>
            {connectionStatus.toUpperCase()}
          </span>
        </div>
        <div className="connection-item">
          <span>Last Update:</span>
          <span>{telemetry.timestamp ? new Date(telemetry.timestamp * 1000).toLocaleTimeString() : 'Never'}</span>
        </div>
      </div>
    </div>
  );
};

export default StatusPanel;
